import { Injectable } from '@angular/core';
import {HttpClient, HttpHeaders} from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class MoviesService {

  api_key = '77d4bdd91e70e1c691bc265726ebb489';

  base_url = 'https://api.themoviedb.org/3';

  constructor(
    private _http: HttpClient,
  ) { }

  getTrending(): Observable<any> {
    let headers : HttpHeaders = new HttpHeaders().set('api_key', this.api_key).set("Access-Control-Allow-Origin", "*").set("Content-Type", "text/plain");

    return this._http.get(this.base_url + '/trending/all/week', {
      headers: headers 
    })
  }

  getMovies(): Observable<any> {
    let headers : HttpHeaders = new HttpHeaders().set('api_key', this.api_key);

    return this._http.get(this.base_url + '/movie/popular', {
      headers: headers 
    })
  }

  getSeries(): Observable<any> {
    let headers : HttpHeaders = new HttpHeaders().set('api_key', this.api_key);

    return this._http.get(this.base_url + '/tv/popular', {
      headers: headers ,
    })
  }
}
