import { Component } from '@angular/core';
import { MoviesService } from 'src/app/services/movies.service';
import { MovieSerie } from 'src/app/shared/interfaces/MovieSerie.interface';

@Component({
  selector: 'app-inicio',
  templateUrl: './inicio.component.html',
  styleUrls: ['./inicio.component.css'],
  providers: [MoviesService]
})

export class InicioComponent {
  movies_series : MovieSerie[] = [
    {
      id: 1,
      name: 'Prueba 1',
      description: 'Follows a fictionalized version of Kevin Hart, as he tries to become an action movie star. He attends a school run by Ron Wilcox, where he attempts to learn the ropes on how to become one of the industrys most coveted action stars.',
      image: "./assets/shang-chi.svg",
      rating: 0,
      category: 'Pelis'
    },
    {
      id: 2,
      name: 'Prueba 2',
      description: 'Prueba description 2',
      image: './assets/black-widow.svg',
      rating: 0,
      category: 'Series'
    },
    {
      id: 3,
      name: 'Prueba 3',
      description: 'Prueba description 3',
      image: './assets/loki.svg',
      rating: 0,
      category: 'Pelis'
    }
  ]

  selected: string = 'Todos';

  paraBuscar: string = '';

  constructor(
    private _moviesService: MoviesService
  ) {}

  ngOnInit() : void {
    this.getTrendingAll();
  }

  getTrendingAll() {
    this._moviesService.getTrending().subscribe({
      next: (data) => {
        console.log(data);
      },
      error: (error) => {
        console.log(error);
      }
    })
  }

  cambiarCategoria(value: string) {
    console.log('Viejo valor del selected', this.selected);
    this.selected = value;
    console.log('Nuevo valor del selected', this.selected);
  }

  buscar(value: string) {
    console.log(value);
  }

}
<section>
    <div class="container">
        <div class="hero">
            <h1>PelisUP!</h1>
            <h2>Plataforma para ver peliculas y series üòÅ</h2>
            <form class="d-flex" role="search">
                <input class="form-control me-2" type="search" placeholder=" üîçBuscar serie o pel√≠cula" aria-label="Search" [(ngModel)]="paraBuscar" (ngModelChange)="buscar(paraBuscar)" [ngModelOptions]="{standalone: true}">
            </form>
            <button type="button" class="btn btn-lg botonTab" (click)="cambiarCategoria('Todos')" [ngClass]="selected == 'Todos' ? 'button-Selected' : 'button-NoSelected' ">Todos</button>
            <button type="button" class="btn btn-lg botonTab" (click)="cambiarCategoria('Pelis')" [ngClass]="selected == 'Pelis' ? 'button-Selected' : 'button-NoSelected' ">Pel√≠culas</button>
            <button type="button" class="btn btn-lg botonTab" (click)="cambiarCategoria('Series')" [ngClass]="selected == 'Series' ? 'button-Selected' : 'button-NoSelected' ">Series</button>
        </div>
    </div>
</section>

<section class="wrapperCards">
    <div class="container">
        <div class="row">
            <div class="col-4" *ngFor="let movie of movies_series">
                <div class="card" *ngIf="selected == movie.category || selected == 'Todos' ">
                    <figure>
                        <img src={{movie.image}}>
                    </figure>
                    <div class="contenido">
                        <h3>{{ movie.name }}</h3>
                    </div>
                </div>
            </div>

        </div>

    </div>
</section>
